mapper.select:
select MEM_ID userId, MEM_NAME userName
from member
where replace(MEM_PHONE,'-','') = #{userPhone}

select MEM_ID userId, MEM_NAME userName
from member
where MEM_ID = #{userId}

mapper.update:
update member
set MEM_PW = #{memPw}
where mem_id = #{memId}

mapper.select:
select sms_num
from sms
where mem_phone = #{userPhone}
