mapper.select:
select rn, memId, memNick, memName, memAddr, memPostNumber, memDetailAdd, memPhone, memReportedCount from (select ROWNUM rn, MEM_ID memId, MEM_NICK memNick, MEM_NAME memName, MEM_ADDR memAddr, MEM_POSTNUMBER memPostNumber, MEM_DETAILADD memDetailAdd, MEM_PHONE memPhone, MEM_REPORTED_COUNT memReportedCount from member
where ${search_option} like '%' ||#{keyword}|| '%'
where MEM_ID like '%' ||#{keyword}|| '%' or MEM_NICK like '%' ||#{keyword}|| '%' or MEM_NAME like '%' ||#{keyword}|| '%'
) where rn between #{startEndPageDTO.startRow} and #{startEndPageDTO.endRow}

SELECT MEM_ID memId, MEM_NICK memNick, MEM_IMAGE memImage, MEM_REPORTED_COUNT memReportedCount 
FROM MEMBER
WHERE MEM_ID = #{memId}

SELECT PROD_NUM prodNum, PROD_NAME prodName, PROD_IMAGE prodImage
FROM PRODUCTS 
WHERE MEM_ID = #{memId}
